import './styles.scss';

//Delay for hero animation
const arrowTime = 6000;

const nav = document.querySelector('nav');
const hero = document.querySelector('.hero-content');
const year = document.getElementById('year');
const toggle = document.getElementById('nav-toggle');

function activateNav() {
  if (nav.classList.contains('nav-active')) {
  } else {
    nav.classList.add('nav-active');
  }
}

(() => {
  // Activate nav after hero animation

  setTimeout(() => {
    activateNav();
  }, arrowTime + 1000);

  //  Active nav if page is scrolled down

  let options1 = {
    root: null,
    rootMargin: '-200px',
    threshold: 0,
  };
  let observer1 = new IntersectionObserver((entries, observer) => {
    entries.forEach((entry) => {
      entry.isIntersecting ? null : activateNav();
    });
  }, options1);

  observer1.observe(hero, options1);

  //  Fade projects in on scroll

  let options2 = {
    root: null,
    rootMargin: '0px',
    threshold: 0.22,
  };
  let observer2 = new IntersectionObserver((entries, observer) => {
    entries.forEach((entry) => {
      if (entry.isIntersecting) {
        // fade in observed elements that are in view
        entry.target.classList.contains('fadeIn')
          ? null
          : entry.target.classList.add('fadeIn');
      }
    });
  }, options2);

  const fadeEls = [
    ...document.querySelectorAll('.cover'),
    ...document.querySelectorAll('.details'),
    document.querySelector('.about-contents'),
    document.querySelector('#contact'),
  ];
  fadeEls.forEach((el) => {
    el.style.opacity = 0; //set inital state
    observer2.observe(el);
  });

  //  Toggle mobile nav

  toggle.addEventListener('click', () => {
    nav.classList.toggle('mobile-nav-open');
  });

  //close on link click
  let links = [...document.querySelectorAll('a')];
  links.forEach((link) =>
    link.addEventListener('click', () => {
      nav.classList.remove('mobile-nav-open');
    })
  );

  //floating labels in form

  let inputs = [
    ...document.querySelectorAll('input'),
    ...document.querySelectorAll('textarea'),
  ];
  inputs.forEach((input) => {
    let label = input.nextElementSibling;
    input.addEventListener('input', (e) => {
      console.log(label);
      console.log(e.target.value);
      if (e.target.value === '') {
        label.classList.remove('show');
      } else {
        label.classList.add('show');
      }
    });
  });

  //  Set current year in footer

  // year.innerText = new Date().getFullYear();

  // Hide nav on scroll down

  let logoText = document.querySelector('.logo_text');

  function hideNavOnScroll() {
    let prevScrollpos = window.pageYOffset;
    let wHeight = window.innerHeight;
    window.onscroll = function () {
      let currentScrollPos = window.pageYOffset;
      if (pageYOffset < wHeight) {
        logoText.classList.contains('hidden')
          ? null
          : logoText.classList.add('hidden');
        return;
      } else {
        logoText.classList.contains('hidden')
          ? logoText.classList.remove('hidden')
          : null;
      }
      if (prevScrollpos > currentScrollPos) {
        document.getElementById('navbar').style.top = '0';
      } else {
        document.getElementById('navbar').style.top = '-100px';
      }
      prevScrollpos = currentScrollPos;
    };
  }
  hideNavOnScroll();
})();
