* {
  padding: 0;
  margin: 0;
  box-sizing: border-box;

  &::selection {
    background: $pink;
  }
}

$MAX-WIDTH: 1066px;
$arrow-time: 8.5s;

@import url('https://fonts.googleapis.com/css2?family=Poppins:wght@300;400&family=Ropa+Sans&display=swap');
html {
  scroll-behavior: smooth;
}
body {
  font-family: 'Ropa Sans', sans-serif;
  // font-family: 'Poppins', sans-serif;

  font-weight: 300;
  // background-color: $yellow;
  background-image: linear-gradient(0deg, $purple, $pink);
  h1,
  h2,
  h3,
  h4,
  a {
    font-family: 'Ropa Sans', sans-serif;
    font-weight: 200;
    letter-spacing: 0.02em;
  }
}
p {
  line-height: 1.15;
}
em {
  font-style: italic;
}

.page-container {
  position: relative;
  max-width: $MAX-WIDTH;
  background-color: $white;
  margin: 0 auto;
  padding: 0 4rem;
  min-height: 100vh;

  //  ---------------------------------------------------//
  //  -----------------   Navigation
  //  ---------------------------------------------------//

  .nav-active {
    .logo {
      animation: rhs-slide-in 0.7s forwards;
    }
    ul {
      animation: lhs-slide-in 0.7s forwards;
    }
  }
  nav {
    // background-color: $;
    font-size: 1.3em;
    // position: absolute;
    z-index: 2;
    position: fixed;
    top: 0;
    left: 50%;
    transform: translateX(-50%);
    width: 100%;
    max-width: $MAX-WIDTH;
    margin: 0 auto;
    padding: 0.4em 0.5em;
    display: flex;
    justify-content: space-between;
    // padding: 2em 2em;
    background-color: $purple;
    .logo {
      opacity: 0;
      color: $white;
    }
    ul {
      width: 40%;
      list-style: none;
      display: grid;
      grid-template-columns: 1fr 1fr 1fr;
      opacity: 0;
      height: 100%;

      li {
        text-align: center;
        height: 100%;
        a {
          display: block;
          padding: 0.3em 0.4em;
          text-decoration: none;
          color: $white;

          transform: all 1s ease;

          &:hover {
            transform: all 1s ease;

          }
        }
      }
    }
  }

  //  ---------------------------------------------------//
  //  -----------------   Header/Hero
  //  ---------------------------------------------------//

  > header {
    position: relative;

    overflow: hidden;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    height: 100vh;
    // background-image: radial-gradient(
    //   circle,
    //   $pink,
    //   $purple 60%,
    //   transparent 80%
    // );

    .title-text {
      display: flex;
      position: absolute;
      width: 100%;
      top: 35%;
      // text-align: center;
      justify-content: center;
      align-items: center;

      opacity: 0;
      animation: fade-in 0.6s ease forwards $arrow-time + 1s;
      font-size: 3.4rem;

      h1,
      h2 {
        width: 100%;
        // opacity: 0;
        font-weight: 200;
        margin: 0 1rem;
      }
      h1 {
        text-align: right;
        color: $purple-dark;
        font-size: 1em;
      }
      h2 {
        text-align: left;
        font-size: 1em;
      }
    }
    .point {
      display: block;
      width: 100%;
      animation: point 1s infinite ease-in-out alternate;
      animation-delay: $arrow-time + 1.4s;
    }
    .text-container {
      position: relative;
      display: block;
      font-size: 2.4rem;
      text-align: center;
      z-index: 1;
      color: inherit;
      text-decoration: none;

      h1,
      h2 {
        opacity: 0;
        font-weight: 200;
        display: block;
        width: 100%;
        animation: fade-in 0.3s ease forwards;
        &:nth-child(6) {
          margin-bottom: 0.9em;
        }

        @for $i from 1 through 6 {
          &:nth-child(#{$i}) {
            animation-delay: #{$i * 1}s;
          }
        }
        @for $i from 7 through 11 {
          &:nth-child(#{$i}) {
            animation-delay: #{5 + ($i * 0.25)}s;
          }
        }
      }
      h1 {
        font-size: 1em;
        margin-bottom: 0.6em;
      }
      h2 {
        font-size: 0.8em;
      }
      animation: arrowflip 1.4s ease forwards;
      animation-delay: $arrow-time;
    }
    .point {
      animation: point 1s infinite ease-in-out alternate;
      animation-delay: $arrow-time + 1.4s;
    }
    .flip-back {
      display: inline-block;
      animation: flip-back 0s forwards ease $arrow-time + 0.3s;
    }
  }
}

//  ---------------------------------------------------//
//  -----------------   Navigation
//  ---------------------------------------------------//

//  ---------------------------------------------------//
//  -----------------   Sections
//  ---------------------------------------------------//

.content {
  h1 {
    color: $purple-dark;
    font-size: 1.8rem;
    margin-bottom: 2rem;
  }
  section {
    &:target::before {
      content: '';
      display: block;
      height: 5rem; // fixed header height -// stops nav from hiding content
      margin: -5rem 0 0; // negative fixed header height
    }
    min-height: 100vh;
    margin: 0rem auto;
    // padding: 2rem;

    > div {
      margin-bottom: 4.4rem;
    }

    //   background-color:opacify($color: $yellow, $amount: 0.9);
